mapper.select:
select e.employee_id, e.dept_id, e.employee_no, e.employee_name, e.employee_sex, e.employee_age, e.employee_phone, e.employee_card_no, e.employee_mail, e.employee_birthday, e.employee_job, e.employee_address, e.employee_expire_time, e.employee_status, e.employee_allowed_ips, e.create_by, ec.employee_name creator_name, e.create_time, e.update_by, ec.employee_name updater_name, e.update_time from t_employee e left join t_employee ec on e.create_by = ec.employee_id left join t_employee eu on e.update_by = eu.employee_id
e.employee_name like concat('%', #{employeeName}, '%')
and e.dept_id = #{deptId}
and e.employee_status = #{employeeStatus}
and e.employee_expire_time >= #{startTime}
and e.employee_expire_time <= #{endTime}
order by e.employee_id desc
